mapper.select:
select 

from CARDJFLOG
where CDLDATE = #{cdldate,jdbcType=DATE}
  and CDLSEQNO = #{cdlseqno,jdbcType=DECIMAL}

  	select  from (
	    select t.*, ROWNUM AS rowno from 
	        (
	        select  
	        from CARDJFLOG 
	         where CDLCID = #{cmcustid, jdbcType=VARCHAR} 
	        order by CDLDATE desc, CDLSEQNO
	        ) t
	    )
	where  rowno >= #{startRow,jdbcType=INTEGER} and ROWNUM <= #{pageSize, jdbcType=INTEGER}

  select 

   from (
    select t.*, ROWNUM AS rowno from 
        (
        select  
        from CARDJFLOG 
         where CDLCURJFFS < 0 AND CDLCID = #{cmcustid, jdbcType=VARCHAR} 
        order by CDLDATE desc, CDLSEQNO
        ) t
    )
where  rowno >= #{startRow,jdbcType=INTEGER} and ROWNUM <= #{pageSize, jdbcType=INTEGER}

  	select  from (
	    select t.*, ROWNUM AS rowno from 
	        (
	        select  
	        from CARDJFLOG 
	         where CDLCURJFFS > 0 AND CDLCID = #{cmcustid, jdbcType=VARCHAR} 
	        order by CDLDATE desc, CDLSEQNO
	        ) t
	    )
	where  rowno >= #{startRow,jdbcType=INTEGER} and ROWNUM <= #{pageSize, jdbcType=INTEGER}

mapper.delete:
delete from CARDJFLOG
where CDLDATE = #{cdldate,jdbcType=DATE}
  and CDLSEQNO = #{cdlseqno,jdbcType=DECIMAL}
